import * as React from 'react';
import logo from './logo.svg';
import './App.css';
import FeaturedPost from './post/components/FeaturedPost';
import {useHttpClient} from './shared/hooks/http-hook';
import PostsSlider from './post/components/PostsSlider';
import ErrorModal from './shared/components/UIElements/ErrorModal';
import LoadingSpinner from './shared/components/UIElements/LoadingSpinner';
import Animation from './shared/components/UIElements/Animation';
import aiRender from './ai-render.json';
import scroll from './scroll.json';
import { CSSTransition } from 'react-transition-group';
const scrollToRef = (ref) => window.scrollTo({
  left:0,
  top:ref.current.offsetTop - 50,
  behavior:"smooth"
}) 

function App() {
  const {isLoading, clearError, error, sendRequest} = useHttpClient();
  const [featuredPost, setFeaturedPost] = React.useState();

  const [loadedPosts, setLoadedPosts] = React.useState();
  const myRef = React.useRef(null)

  const [text, setText] = React.useState("")
const [fullText, setFullText] = React.useState(
    "Discover a Blog."
  )
const [index, setIndex] = React.useState(0)

React.useEffect(()=>{
    if (index < fullText.length) {
      setTimeout(() => {
        setText(text + fullText[index])
        setIndex(index + 1)
      }, 100)
    }
  },[index]); 

  React.useEffect(()=>{

    const fetchData = async () => {

        try{
            const responsePosts = await sendRequest('http://localhost:8000/api/posts');
            setLoadedPosts(responsePosts.posts.filter(p => p.category.title === "Published").reverse());
            setFeaturedPost(responsePosts.posts[0]);
        }catch(err){
                console.log(err)
        }
    };
    fetchData();
},[sendRequest]); 

  const slideHandler = event => {
    const postId = event.target.id;
    const pickedPost = loadedPosts.find(p => p.id === postId);
    console.log(postId)
    setFeaturedPost(pickedPost);
    scrollToRef(myRef)
  }
const executeScroll = () => {
  scrollToRef(myRef)
}
  return (<React.Fragment>
    <ErrorModal error={error} onClear={clearError} />
    {isLoading && <LoadingSpinner asOverlay />}
    <div className="App">
      <header className="App-header" onClick={executeScroll}>
      <CSSTransition
                    in={text.length === 0}
                    timeout={0}
                    classNames="slide"
                    mountOnEntering
                >

        <img src={logo} width={150} alt="logo" />
        </CSSTransition>
        <h2 style={{color:"#FF2E4C"}}>
        {text}
        </h2>
        <CSSTransition
                    in={text.length === fullText.length}
                    timeout={2500}
                    classNames="slide"
                    mountOnEnter
                >
                   <p>Written by AI.</p>
                </CSSTransition>
                <CSSTransition
                    in={text.length === fullText.length}
                    timeout={5000}
                    classNames="slide"
                    mountOnEnter
                >
        <Animation data={scroll} height={280} />

                </CSSTransition>
      </header>
      <main>
        <div ref={myRef} className="posts-wrapper">
        {featuredPost && <FeaturedPost item={featuredPost} />}
        </div>
        <div className="posts-wrapper">
        {loadedPosts && <PostsSlider onClick={slideHandler} items={loadedPosts.filter(p => p.id !== featuredPost.id)} />}
        </div>
      </main>
    </div>

    </React.Fragment>
  );
}

export default App;
