import * as React from 'react'
import Slider from 'react-slick';
import {useWindowSize} from '../../hooks/size-hook';
import './AppBanner.css';
const AdBanner = (props) => {

    const size = useWindowSize();

    const Settings = {
        infinite: true,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        speed: 600,
        autoplaySpeed:8000,
        prevArrow: false,
        nextArrow: false,
        cssEase: "linear",
        fade: true
    }

    const createAds = (data, index) => {
        return(<>
                        <a href={data.content} key={index} id={data.id}><img src={data.image} alt={data.title} /></a>
        </>

        )
    }

return(
    <Slider className="ad-slider" {...Settings}>
        {size.width < 400 && props.mobileAds.map(createAds)}
        {size.width > 400 && props.desktopAds.map(createAds)}

    </Slider>
)
}
export default AdBanner;
