import * as React from 'react';
import logo from './logo.svg';
import './App.css';
import FeaturedPost from './post/components/FeaturedPost';
import {useHttpClient} from './shared/hooks/http-hook';
import PostsSlider from './post/components/PostsSlider';
function App() {
  const {isLoading, clearError, error, sendRequest} = useHttpClient();
  const [featuredPost, setFeaturedPost] = React.useState({
    id: '1',
    title: 'default Post',
    content: 'lorem ipsum dolor sit amet, consectetur adipiscing elit sed, sed do eiusmod tempor incididunt ut labore et.',
    image: 'https://picsum.photos/600/400',
    creator: 'Anonymous',
    category: 'default'
  });

  const [loadedPosts, setLoadedPosts] = React.useState();

  React.useEffect(()=>{
    const fetchData = async () => {

        try{
            const responsePosts = await sendRequest('http://localhost:8000/api/posts');
            setLoadedPosts(responsePosts.posts.filter(p => p.published === true).reverse());
            setFeaturedPost(responsePosts.posts[0]);
        }catch(err){
                console.log(err)
        }
    };
    fetchData();
},[sendRequest]);



  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          The ultimative AI Blog experiment. <br /> Check out our Posts written by AI below.
        </p>
      </header>
      <main>
        <div className="posts-wrapper">
        <FeaturedPost item={featuredPost} />
        </div>
        <div className="posts-wrapper">
        <PostsSlider items={loadedPosts} />
        </div>
      </main>
    </div>
  );
}

export default App;
